function calculateFileName(){var e={first:$("#firstName").val(),last:$("#lastName").val()},a=$("#documentTitle").val(),t=$("#documentYear").val(),l=$("#format").val(),n="";return n=e.last+"-"+a+"_"+t+".html"+l,n=n.replace(/[^A-Za-z0-9\s-.]/g,"").replace(/\s/g,"_").toLowerCase()}$(document).ready(function(){$("form").eq(0).submit(function(e){return e.preventDefault(),!1}),$("#create").on("click",function(){$("#file-name .file-name").html(calculateFileName())})});